;(function($){$.fn.equalheightcols=function(options){return this.each(function(){var o=$.extend({},$.fn.equalheightcols.defaults,options),$this=$(this);var shortestHeight=10000;$this.find(o.itemsToEqualize).each(function(){shortestHeight=$(this).height()<shortestHeight?$(this).height():shortestHeight;});$this.find(o.itemsToEqualize).each(function(){if($(this).height()==shortestHeight)return;while($(this).height()>shortestHeight){$(this).html($.trim($(this).html()).replace(/ \S*$/,''));}if($.trim($(this).html()).slice(-1)!='.'&&o.threeDots){$(this).html($.trim($(this).html()).replace(/ \S*$/,'')+'...');}});});};$.fn.equalheightcols.defaults={itemsToEqualize:'p',threeDots:true};})(jQuery);